import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    console.log("hello i am constructor from News component");
    super();
    this.state = {
      articles: [],
      loading: false,
    };
  }

  async componentDidMount() {
    console.log("cdm");
    let url =
      "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=c639f84c03a742aa818c23cf1db8efbe";
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles });
  }

  render() {
    return (
      <div className="container my-3">
        <h2>GlobeGlimpse - Top Headlines </h2>
        This is a news component
        <div className="row">
          {this.state.articles.map((element) => {
            //to get articles from api
            return (
              //because url is unique
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title ? element.title.slice(0, 44) : ""}
                  description={
                    element.title ? element.description.slice(0, 88) : ""
                  }
                  imageurl={
                    element.urlToImage
                      ? element.urlToImage
                      : "https://cdn.crash.net/styles/large_article/s3/pa/3264825.0064.jpg?itok=fHwhqRo2"
                  }
                  newsurl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="container  d-flex justify-content-between">
          <button
            type="button"
            class="btn btn-dark"
            onClick={this.handlePrevClick}
          >
            {" "}
            &larr; Previous
          </button>
          <button
            type="button"
            class="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
