import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    console.log("hello i am constructor from News component");
    super();
    this.state = {
      articles: [],
      loading: false,
    };
  }

  async componentDidMount() {
    console.log("cdm");
    let url =
      "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=27486b0ba3e349338074dea6a1f32699";
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles });
  }

  handlePrevClick = () => {
    console.log("prev");
  };

  handleNextClick = () => {
    console.log("Next");
  };

  render() {
    return (
      <div className="container my-3">
        <h2>GlobeGlimpse - Top Headlines </h2>
        This is a news component
        <div className="row">
          {this.state.articles.map((element) => {
            //to get articles from api
            return (
              //because url is unique
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title}
                  description={element.description}
                  imageurl={
                    element.urlToImage
                      ? element.urlToImage
                      : "https://cdn.crash.net/styles/large_article/s3/pa/3264825.0064.jpg"
                  }
                  newsurl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="container d-flex justify-content-between ">
          <button
            disabled={this.state.page <= 1}
            type="button"
            class="btn btn-dark"
            onClick={this.handlePrevClick}
          >
            &larr; Previous
          </button>
          <button
            type="button"
            class="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
